// BUILD COMPLETE INVESTOR DATASET
// ═══════════════════════════════════════════════════════════════

export function buildInitialInvestors() {
  const investors = [];
  let id = 1;

  // 1. Angels
  for (const a of ANGELS) {
    investors.push({
      id: id++, name: a.name, company: "", email: "", type: "angel",
      location: "", focus: "", aum: "", website: "", twitter: "",
      notes: a.commitment > 0 ? `Committed: $${a.commitment.toLocaleString()}` : "Commitment TBD",
      commitment: a.commitment,
      priority: a.commitment >= 100000 ? "high" : "medium",
      stage: a.commitment > 0 ? "committed" : "identified",
      pitchAngle: "founder-pedigree",
      source: "angel-list",
      lastContact: null, nextAction: a.commitment > 0 ? "Send closing docs" : "Follow up on commitment",
      activities: [],
    });
  }

  // 2. Sui Ecosystem
  for (const s of SUI_INVESTORS) {
    const iType = classifyInvestorType({ type: s.type, name: s.name, notes: s.notes });
    investors.push({
      id: id++, name: s.name, company: s.name, email: "", type: iType,
      location: "", focus: s.round, aum: "", website: s.website, twitter: s.twitter,
      notes: `${s.notes} | Sui ${s.round}`,
      commitment: 0, priority: s.priority,
      stage: "identified",
      pitchAngle: suggestPitchAngle(iType),
      source: "sui-ecosystem",
      lastContact: null, nextAction: "Research contact person → warm intro via Sui Foundation",
      activities: [],
    });
  }

  // 3. Direct Contacts
  for (const c of DIRECT_CONTACTS) {
    const iType = classifyInvestorType({ type: "venture capital", name: c.firm });
    investors.push({
      id: id++, name: `${c.first} ${c.last}`, company: c.firm, email: c.email, type: iType,
      location: "", focus: "", aum: "", website: "", twitter: "",
      notes: "", commitment: 0, priority: "medium",
      stage: "identified",
      pitchAngle: suggestPitchAngle(iType),
      source: "direct-contacts",
      lastContact: null, nextAction: c.email ? "Draft personalized outreach" : "Find email",
      activities: [],
    });
  }

  // 4. Enhanced Database
  for (const e of ENHANCED_DB) {
    const iType = classifyInvestorType({ type: e.type, name: e.company, notes: e.aum });
    investors.push({
      id: id++, name: e.name, company: e.company, email: e.email, type: iType,
      location: e.location, focus: e.focus, aum: e.aum, website: "", twitter: "",
      notes: e.aum ? `AUM: ${e.aum}` : "",
      commitment: 0,
      priority: (e.aum && (e.aum.includes("B+") || e.aum.includes("T+"))) ? "high" : "medium",
      stage: "identified",
      pitchAngle: suggestPitchAngle(iType),
      source: "enhanced-database",
      lastContact: null, nextAction: "Research crypto/DeFi interest → personalized outreach",
      activities: [],
    });
  }

  // 5. Inception Funds
  for (const f of INCEPTION_FUNDS) {
    investors.push({
      id: id++, name: f, company: f, email: "", type: "inception-fund",
      location: "US", focus: "Pre-seed / Inception", aum: "", website: "", twitter: "",
      notes: "Writes checks at inception stage",
      commitment: 0, priority: "medium",
      stage: "identified",
      pitchAngle: "neutral-auction",
      source: "inception-funds",
      lastContact: null, nextAction: "Research crypto/DeFi thesis fit → find partner contact",
      activities: [],
    });
  }

  // 6. Web3 VCs
  for (const w of WEB3_VCS) {
    investors.push({
      id: id++, name: w.name, company: w.name, email: "", type: "crypto-vc",
      location: "", focus: "Web3/DeFi", aum: "", website: w.website, twitter: w.twitter,
      notes: w.desc,
      commitment: 0, priority: "medium",
      stage: "identified",
      pitchAngle: "jito-for-sui",
      source: "web3-vc-list",
      lastContact: null, nextAction: "Check Sui/MEV portfolio overlap → find partner email",
      activities: [],
    });
  }

  return investors;
}
